import React from 'react';

function Home() {

    const [recipes, setRecipes] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [userId] = useState('user003');

  useEffect(() => {
    // Получить все рецепты при загрузке компонента
    getRecipes()
      .then((data) => {
        console.log('Полученные рецепты:', data);
        setRecipes(data);
        setFilteredRecipes(data);
      })
      .catch((err) => console.error(err));
  }, [userId]);

  useEffect(() => {
    getUserRecommendations(userId)
      .then((data) => {
        console.log('Полученные рекомендации:', data);
        setRecommendations(data.data); // Извлекаем массив рекомендаций
      })
      .catch((err) => console.error('Ошибка при получении рекомендаций:', err));
  }, [userId]);
  
  const handleAddToFavorites = (recipeId) => {
    addRecipeToFavorites(userId, recipeId)
      .then(() => {
        alert('Рецепт добавлен в избранное!');
      })
      .catch(err => console.error(err));
  };

  // Обработчик изменения поискового запроса
  const handleSearchQueryChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Обработчик кнопки поиска
  const handleSearch = () => {
    if (searchQuery.trim() === '') {
      setFilteredRecipes(recipes); // Если запрос пустой, показываем все рецепты
      return;
    }

    // Настройка Fuse.js для поиска
    const options = {
      keys: ['name', 'ingredients.name'], // Ищем по названию рецепта и ингредиентам
      threshold: 0.4 // Порог для учета опечаток
    };
    const fuse = new Fuse(recipes, options);
    const result = fuse.search(searchQuery);
    setFilteredRecipes(result.map(resultItem => resultItem.item));
  };


    return (
        <div className="App">
            <header className="header">
                <h1 className="catalog-title">Каталог Рецептов</h1>
                <div className="search-bar">
                <input
                    type="text"
                    placeholder="Поиск рецептов или ингредиентов..."
                    value={searchQuery}
                    onChange={handleSearchQueryChange}
                />
                <button onClick={handleSearch}>Поиск</button>
                </div>
            </header>
            
            {/* Блок с рекомендованными рецептами */}
            <div className="recommended-block">
                <h2>Рекомендованные Рецепты</h2>
                {Array.isArray(recommendations) && recommendations.length === 0 ? (
                <p>Нет рекомендаций для отображения.</p>
                ) : (
                <div className="recommended-recipes">
                    {Array.isArray(recommendations) && recommendations.slice(0, 3).map((recipe) => (
                    <div key={recipe.recipe_id} className="recommended-recipe-card">
                        <h4>{recipe.name}</h4>
                        <p><strong>Категория:</strong> {recipe.category}</p>
                        <button onClick={() => handleAddToFavorites(recipe.recipe_id)}>
                        Добавить в избранное
                        </button>
                    </div>
                    ))}
                </div>
                )}
            </div>


            {/* Блок с полным каталогом рецептов */}
            <div className="recipe-catalog">
                {recipes.length === 0 ? (
                    <p>Нет рецептов для отображения.</p>
                    ) : (
                    recipes.map((recipe) => (
                        <div key={recipe.recipe_id} className="recipe-card">
                        <h3>{recipe.name}</h3>
                        <p><strong>Категория:</strong> {recipe.category}</p>
                        <p><strong>Время приготовления:</strong> {recipe.cook_time} минут</p>
                        <p><strong>Ингредиенты:</strong></p>
                        <ul>
                            {recipe.ingredients.map((ingredient, index) => (
                            <li key={index}>
                                {ingredient.name} — {ingredient.quantity}
                            </li>
                            ))}
                        </ul>
                        <button onClick={() => handleAddToFavorites(recipe.recipe_id)}>
                            Добавить в избранное
                        </button>
                        </div>
                        ))
                )}
            </div>
        </div>
      );


}

export default Home;
