import React, { useEffect, useState } from 'react';
import { getRecipes, getUserRecommendations, addRecipeToFavorites } from './api/api';

function App() {
  const [recipes, setRecipes] = useState([]);
  const [recommendations, setRecommendations] = useState([]);
  const [userId] = useState('user003'); // Пример пользователя, которого мы используем

  useEffect(() => {
    // Получить все рецепты при загрузке компонента
    getRecipes()
      .then(response => setRecipes(response.data))
      .catch(err => console.error(err));

    // Получить рекомендации для пользователя
    getUserRecommendations(userId)
      .then(response => setRecommendations(response.data))
      .catch(err => console.error(err));
  }, [userId]);

  const handleAddToFavorites = (recipeId) => {
    addRecipeToFavorites(userId, recipeId)
      .then(() => {
        alert('Рецепт добавлен в избранное!');
      })
      .catch(err => console.error(err));
  };

  return (
    <div className="App">
      <h1>Рецепты</h1>
      <div>
        {recipes.length === 0 ? (
          <p>Нет рецептов для отображения.</p>
        ) : (
          recipes.map((recipe) => (
            <div key={recipe.recipe_id}>
              <h3>{recipe.name}</h3>
              <p>Категория: {recipe.category}</p>
              <button onClick={() => handleAddToFavorites(recipe.recipe_id)}>
                Добавить в избранное
              </button>
            </div>
          ))
        )}
      </div>

      <h2>Рекомендации для вас</h2>
      <div>
        {recommendations.length === 0 ? (
          <p>Нет рекомендаций для отображения.</p>
        ) : (
          recommendations.map((recipe) => (
            <div key={recipe.recipe_id}>
              <h3>{recipe.name}</h3>
              <p>Категория: {recipe.category}</p>
              <button onClick={() => handleAddToFavorites(recipe.recipe_id)}>
                Добавить в избранное
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );  
}

export default App;
