// src/api/api.js
import axios from 'axios';

const API_URL = 'http://localhost:3000';

export const getRecipes = async () => {
  return await axios.get(`${API_URL}/recipes`);
};

export const getUserRecommendations = async (userId) => {
  return await axios.get(`${API_URL}/users/${userId}/recommendations`);
};

export const addRecipeToFavorites = async (userId, recipeId) => {
  return await axios.post(`${API_URL}/users/${userId}/favorites`, { recipe_id: recipeId });
};
