import React, { useState } from 'react';
import axios from 'axios';

const SignUp = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSignUp = async () => {
    try {
      const response = await axios.post('http://localhost:3000/register', { email, password });
      alert(response.data.message);
    } catch (error) {
      console.error('Ошибка при регистрации:', error.response.data.message);
      alert('Ошибка при регистрации: ' + error.response.data.message);
    }
  };

  return (
    <div className="form-container">
      <h2>Регистрация</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Пароль"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleSignUp}>Зарегистрироваться</button>
    </div>
  );
};

export default SignUp;
